package com.renato.imc.model;public class Conta {    private int numero;    public double saldo;    public double creditar;    private double debitar;    public Conta(int numero){        this.numero = numero;    }    public int getNumero() {        return numero;    }    public double getSaldo() {        return saldo;    }    public void creditar(double v) {        this.saldo = v + saldo ;    }    public void debitar(double v){        this.saldo = saldo - v ;        if(this.saldo < 0 )            saldo = saldo -10;    }}