package com.renato.imc.model;import java.util.ArrayList;public class Informe {    public double montante;    public String categoria;    public double montante(Cliente c) {        double total = 0;        ArrayList<Conta> contas =  c.getCarteira();        for (Conta ctemp : contas ) {            total = total + ctemp.getSaldo();        }        return total;    }    public String categoria(Cliente c) {        double total = 0;        String categoria = "PADRAO";        ArrayList<Conta> contas =  c.getCarteira();        for (Conta ctemp : contas ) {            total = total + ctemp.getSaldo();        }        if(total > 100000)            categoria = "INVESTIDOR";        if(total < 0)            categoria = "DEVEDOR";        return categoria;    }}